pragma solidity ^0.4.24;

import '../base/StandardToken.sol';
import '../base/SafeMath.sol';
import '../base/Math.sol';

/// @title Mintable token used for tests and demos.
contract DemoToken is StandardToken {
	using SafeMath for uint256;

	uint8 public constant decimals = 18;

	constructor() public {}

	function mint(address to, uint256 amount) external {
		if (totalSupply_ + amount > totalSupply_)
			totalSupply_ += amount;
		balances[to] = balances[to].add(amount);
		emit Transfer(0x0, to, amount);
	}

	function batchMint(address[] dsts, uint256[] amounts) external {
		require(dsts.length == amounts.length);
		for (uint256 i = 0; i < dsts.length; i++) {
			if (totalSupply_ + amounts[i] > totalSupply_)
				totalSupply_ += amounts[i];
			else
				totalSupply_ = uint256(-1);
			balances[dsts[i]] = balances[dsts[i]].add(amounts[i]);
			emit Transfer(0x0, dsts[i], amounts[i]);
		}
	}
}
